package docs

import (
	"embed"
	"fmt"
)

//go:embed "swagger.json" "redoc.standalone.js"
var sourcesFS embed.FS

type Redocly struct {
	Html string
}

type RedoclySource struct {
	Swagger       string
	RedoclyScript string
	Favicon       string
	Logo          string
	Html          string
}

func NewRedoclyHtml(title, path string) *Redocly {
	sources := &RedoclySource{
		Swagger:       sourceData("swagger.json"),
		RedoclyScript: sourceData("redoc.standalone.js"),
		Favicon:       xFavicon,
		Logo:          xLogo,
		Html:          xHTML,
	}

	return &Redocly{
		Html: fmt.Sprintf(
			sources.Html,
			title, sources.Favicon, sources.RedoclyScript, sources.Swagger, sources.Logo, title, path,
		),
	}
}

func sourceData(filename string) string {
	data, _ := sourcesFS.ReadFile(filename)
	return string(data)
}

